  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '标题',
  `content` text COLLATE utf8_unicode_ci NOT NULL COMMENT '正文',
  `classid` int(11) NOT NULL COMMENT '分类',
  `createdate` date NOT NULL COMMENT '创建时间',
  `modifydate` date NOT NULL COMMENT '修改时间',
  `clicktimes` int(11) NOT NULL COMMENT '点击次数',
